// frontend/src/App.js (Side-by-Side Layout)

import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // We'll keep this for custom styles

import {
  Container, Box, Typography, TextField, Button,
  Card, CardContent, IconButton, Snackbar, Alert
} from '@mui/material';

import { Code, ContentCopy, Download } from '@mui/icons-material';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { vscDarkPlus as syntaxTheme } from 'react-syntax-highlighter/dist/esm/styles/prism';

function App() {
  // --- UPDATED STATE: Simplified to a single language selection ---
  const [selectedLanguage, setSelectedLanguage] = useState('javascript-web');
  const [prompt, setPrompt] = useState('');
  const [generatedCode, setGeneratedCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // --- UPDATED LANGUAGES: Removed CSS, added JS Web Page ---
  const languages = [
    { id: 'javascript-web', label: 'JS Web Page', icon: '🌐' },
    { id: 'html', label: 'HTML', icon: '📄' },
    { id: 'javascript', label: 'JavaScript', icon: '⚡' },
    { id: 'python', label: 'Python', icon: '🐍' }
  ];

  const generateCode = async () => {
    if (!prompt.trim()) {
      setSnackbar({ open: true, message: 'Please enter a description', severity: 'warning' });
      return;
    }
    setLoading(true);
    setGeneratedCode('');
    try {
      // Pass the single selected language to the backend
      const response = await axios.post('http://localhost:5000/api/generate', {
        prompt,
        language: selectedLanguage
      });
      setGeneratedCode(response.data.code);
    } catch (error) {
      setSnackbar({ open: true, message: 'Error generating code. Please try again.', severity: 'error' });
    }
    setLoading(false);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(generatedCode);
    setSnackbar({ open: true, message: 'Code copied!', severity: 'success' });
  };

  const downloadCode = () => {
    const fileExtension = selectedLanguage === 'javascript-web' ? 'html' : selectedLanguage;
    const element = document.createElement('a');
    const file = new Blob([generatedCode], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = `generated-code.${fileExtension}`;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  // Logic to determine if the preview panel should be shown
  const canShowPreview = generatedCode && (selectedLanguage === 'html' || selectedLanguage === 'javascript-web');

  return (
    <Container maxWidth="xl" className="app-container">
      <Box className="header">
        <Typography variant="h1"><Code /> AI Code Generator</Typography>
        <Typography variant="subtitle1">Generate code in multiple languages with AI assistance</Typography>
      </Box>

      <Card className="code-generation-card">
        <CardContent>
          <Box className="language-selector">
            {languages.map(lang => (
              <Button
                key={lang.id}
                onClick={() => setSelectedLanguage(lang.id)}
                variant={selectedLanguage === lang.id ? 'contained' : 'outlined'}
                startIcon={<span>{lang.icon}</span>}
              >
                {lang.label}
              </Button>
            ))}
          </Box>
          <TextField
            className="prompt-input" fullWidth multiline rows={4} variant="outlined"
            placeholder="E.g., A simple to-do list app with an add button"
            value={prompt} onChange={(e) => setPrompt(e.target.value)}
          />
          <Button className="generate-button" onClick={generateCode} disabled={loading} variant="contained" size="large" fullWidth>
            {loading ? 'Generating...' : '✨ Generate Code'}
          </Button>
        </CardContent>
      </Card>

      {/* --- THIS IS THE NEW LAYOUT FOR CODE AND PREVIEW --- */}
      {generatedCode && (
        <div className="row mt-4">
          {/* Left Column: Generated Code */}
          <div className={canShowPreview ? 'col-lg-6' : 'col-12'}>
            <Card className="code-output-card" sx={{ height: '100%' }}>
              <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                  <Typography variant="h6">Generated Code</Typography>
                  <Box>
                    <IconButton onClick={copyToClipboard} color="primary" title="Copy"><ContentCopy /></IconButton>
                    <IconButton onClick={downloadCode} color="primary" title="Download"><Download /></IconButton>
                  </Box>
                </Box>
                <Box sx={{ height: 450, overflow: 'auto', background: '#282c34', borderRadius: 1 }}>
                  <SyntaxHighlighter
                    language={selectedLanguage === 'javascript-web' ? 'html' : selectedLanguage}
                    style={syntaxTheme}
                    showLineNumbers
                    customStyle={{ margin: 0, height: '100%' }}
                  >
                    {generatedCode}
                  </SyntaxHighlighter>
                </Box>
              </CardContent>
            </Card>
          </div>

          {/* Right Column: Live Preview (only shown when applicable) */}
          {canShowPreview && (
            <div className="col-lg-6">
              <Card sx={{ height: '100%' }}>
                <CardContent>
                  <Typography variant="h6" gutterBottom>Live Preview</Typography>
                  <iframe
                    className="code-preview-iframe" // Ensure this class is in App.css
                    srcDoc={generatedCode}
                    title="Live Preview"
                    sandbox="allow-scripts allow-forms"
                    style={{ height: '495px' }} // Adjusted height to match
                  />
                </CardContent>
              </Card>
            </div>
          )}
        </div>
      )}

      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={() => setSnackbar({ ...snackbar, open: false })}>
        <Alert severity={snackbar.severity}>{snackbar.message}</Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
