// frontend/src/App.js (Updated)

import React, { useState } from 'react';
import axios from 'axios';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { atomDark } from 'react-syntax-highlighter/dist/esm/styles/prism';
import { Bot, Clipboard, Download, Code, Brush, FileCode, MonitorPlay } from 'lucide-react';
import './App.css';

function App() {
  // --- UPDATED: Default language is now 'javascript-web' ---
  const [language, setLanguage] = useState('javascript-web');
  const [prompt, setPrompt] = useState('');
  const [generatedCode, setGeneratedCode] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  // --- UPDATED: Language list now includes 'JavaScript Web Page' ---
  // and other names are more descriptive.
  const languageConfig = {
    'javascript-web': { name: 'JavaScript Web Page', syntax: 'html', extension: 'html', icon: <MonitorPlay size={20} /> },
    html: { name: 'HTML (Structure Only)', syntax: 'markup', extension: 'html', icon: <FileCode size={20} /> },
    css: { name: 'CSS (Styles Only)', syntax: 'css', extension: 'css', icon: <Brush size={20} /> },
    javascript: { name: 'JavaScript (Logic Only)', syntax: 'javascript', extension: 'js', icon: <Code size={20} /> },
    python: { name: 'Python', syntax: 'python', extension: 'py', icon: <Code size={20} /> },
  };

  const handleGenerateCode = async () => {
    if (!prompt) {
      setError('Please enter a prompt.');
      return;
    }
    setIsLoading(true);
    setError('');
    setGeneratedCode('');

    try {
      const response = await axios.post('http://127.0.0.1:5000/api/generate', {
        prompt: prompt,
        language: language,
      });
      setGeneratedCode(response.data.code);
    } catch (err) {
      setError('Failed to generate code. Please check the backend server.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  const copyToClipboard = () => {
    if (!generatedCode) return;
    navigator.clipboard.writeText(generatedCode)
      .then(() => alert('Code copied to clipboard!'))
      .catch(err => console.error('Failed to copy: ', err));
  };

  const downloadCode = () => {
    if (!generatedCode) return;
    const { extension } = languageConfig[language];
    const blob = new Blob([generatedCode], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `code.${extension}`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="container-fluid vh-100 d-flex flex-column p-4 app-container">
      <header className="header text-center">
        <h1>
          <Bot size={40} className="me-2" /> AI Code Generator
        </h1>
        <p className="text-muted">Powered by React, Flask & Groq</p>
      </header>

      <main className="row flex-grow-1">
        {/* Left Column: Input Form */}
        <div className="col-md-5 d-flex flex-column">
          <div className="mb-3">
            <label htmlFor="prompt-input" className="form-label">Your Prompt</label>
            <textarea
              id="prompt-input"
              className="form-control"
              rows="8"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder="e.g., A simple todo list app with a text input and an add button"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="language-select" className="form-label">Language</label>
            <select
              id="language-select"
              className="form-select"
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
            >
              {Object.entries(languageConfig).map(([key, { name }]) => (
                <option key={key} value={key}>{name}</option>
              ))}
            </select>
          </div>
          <button
            className="btn btn-primary w-100"
            onClick={handleGenerateCode}
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                <span className="ms-2">Generating...</span>
              </>
            ) : 'Generate Code'}
          </button>
          {error && <div className="alert alert-danger mt-3">{error}</div>}
        </div>

        {/* Right Column: Code Output & Preview */}
        <div className="col-md-7 d-flex flex-column">
          <ul className="nav nav-tabs" id="myTab" role="tablist">
            <li className="nav-item" role="presentation">
              <button className="nav-link active" id="code-tab" data-bs-toggle="tab" data-bs-target="#code-panel" type="button" role="tab" aria-controls="code-panel" aria-selected="true">
                <Code size={16} className="me-1" /> Code
              </button>
            </li>
            {/* --- UPDATED: Condition to show Live Preview tab --- */}
            {(language === 'html' || language === 'javascript-web') && (
              <li className="nav-item" role="presentation">
                <button className="nav-link" id="preview-tab" data-bs-toggle="tab" data-bs-target="#preview-panel" type="button" role="tab" aria-controls="preview-panel" aria-selected="false">
                  <MonitorPlay size={16} className="me-1" /> Live Preview
                </button>
              </li>
            )}
          </ul>

          <div className="tab-content flex-grow-1" id="myTabContent">
            {/* Code Panel */}
            <div className="tab-pane fade show active h-100" id="code-panel" role="tabpanel" aria-labelledby="code-tab">
              <div className="output-card">
                <div className="output-header">
                  <span>{languageConfig[language].icon} {languageConfig[language].name}</span>
                  <div className="btn-group">
                    <button className="btn btn-sm btn-outline-secondary" onClick={copyToClipboard} title="Copy Code">
                      <Clipboard size={16} />
                    </button>
                    <button className="btn btn-sm btn-outline-secondary" onClick={downloadCode} title="Download File">
                      <Download size={16} />
                    </button>
                  </div>
                </div>
                <div className="code-container">
                  {generatedCode ? (
                    <SyntaxHighlighter
                      language={languageConfig[language].syntax}
                      style={atomDark}
                      showLineNumbers
                      wrapLines={true}
                      customStyle={{ height: '100%', backgroundColor: 'var(--card-bg-color)' }}
                    >
                      {generatedCode}
                    </SyntaxHighlighter>
                  ) : (
                    <div className="placeholder-text">
                      {isLoading ? 'AI is thinking...' : 'Your generated code will appear here.'}
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Preview Panel */}
            <div className="tab-pane fade h-100" id="preview-panel" role="tabpanel" aria-labelledby="preview-tab">
              <iframe
                className="live-preview"
                srcDoc={generatedCode}
                title="HTML Live Preview"
                sandbox="allow-scripts allow-forms" // Added allow-forms for better compatibility
              />
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
