// frontend/src/App.js (Updated UI Implementation)

import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

import {
  Container, Box, Typography, TextField, Button,
  Card, CardContent, IconButton, Snackbar, Alert
} from '@mui/material';

import { Code, ContentCopy, Download } from '@mui/icons-material';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { vscDarkPlus as syntaxTheme } from 'react-syntax-highlighter/dist/esm/styles/prism';

function App() {
  const [selectedLanguages, setSelectedLanguages] = useState(['javascript']);
  const [prompt, setPrompt] = useState('');
  const [generatedCode, setGeneratedCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  const languages = [
    { id: 'html', label: 'HTML', icon: 'ðŸŒ' },
    { id: 'javascript', label: 'JavaScript', icon: 'âš¡' },
    { id: 'python', label: 'Python', icon: 'ðŸ' },
    { id: 'css', label: 'CSS', icon: 'ðŸŽ¨' }
  ];

  const handleLanguageToggle = (lang) => {
    if (selectedLanguages.includes(lang)) {
      if (selectedLanguages.length > 1) {
        setSelectedLanguages(selectedLanguages.filter(l => l !== lang));
      }
    } else {
      setSelectedLanguages([...selectedLanguages, lang]);
    }
  };

  const generateCode = async () => {
    if (!prompt.trim()) {
      setSnackbar({
        open: true,
        message: 'Please enter a description of what you want to create',
        severity: 'warning'
      });
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/generate', {
        prompt,
        languages: selectedLanguages
      });
      setGeneratedCode(response.data.code);
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Error generating code. Please try again.',
        severity: 'error'
      });
    }
    setLoading(false);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(generatedCode);
    setSnackbar({
      open: true,
      message: 'Code copied to clipboard!',
      severity: 'success'
    });
  };

  const downloadCode = () => {
    const element = document.createElement('a');
    const file = new Blob([generatedCode], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = `generated-code.${selectedLanguages[0]}`;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  return (
    <Container maxWidth="lg" className="app-container">
      <Box className="header">
        <Typography variant="h1">
          <Code /> AI Code Generator
        </Typography>
        <Typography variant="subtitle1">
          Generate code in multiple languages with AI assistance
        </Typography>
      </Box>

      <Card className="code-generation-card">
        <CardContent>
          <Box className="language-selector">
            {languages.map(lang => (
              <Button
                key={lang.id}
                onClick={() => handleLanguageToggle(lang.id)}
                className={`language-button ${selectedLanguages.includes(lang.id) ? 'selected' : ''}`}
                startIcon={<span>{lang.icon}</span>}
              >
                {lang.label}
              </Button>
            ))}
          </Box>

          <TextField
            className="prompt-input"
            fullWidth
            multiline
            rows={4}
            variant="outlined"
            placeholder="E.g., Create a responsive login form with validation..."
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
          />

          <Button
            className="generate-button"
            onClick={generateCode}
            disabled={loading}
          >
            {loading ? 'Generating...' : 'âœ¨ Generate Code'}
          </Button>
        </CardContent>
      </Card>

      {generatedCode && (
        <Box className="code-output">
          <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 1, mb: 1 }}>
            <IconButton onClick={copyToClipboard} color="primary">
              <ContentCopy />
            </IconButton>
            <IconButton onClick={downloadCode} color="primary">
              <Download />
            </IconButton>
          </Box>
          <SyntaxHighlighter
            language={selectedLanguages[0]}
            style={syntaxTheme}
            showLineNumbers
          >
            {generatedCode}
          </SyntaxHighlighter>
        </Box>
      )}

      <Box className="features-grid">
        <Card className="feature-card">
          <Typography variant="h6">Multi-Language Support</Typography>
          <Typography variant="body2">
            Generate code in HTML, JavaScript, Python, and CSS
          </Typography>
        </Card>
        <Card className="feature-card">
          <Typography variant="h6">Live Preview</Typography>
          <Typography variant="body2">
            See your HTML code rendered in real-time
          </Typography>
        </Card>
        <Card className="feature-card">
          <Typography variant="h6">Export & Copy</Typography>
          <Typography variant="body2">
            Easily copy or download your generated code
          </Typography>
        </Card>
      </Box>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert severity={snackbar.severity}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
