// frontend/src/App.js (Corrected Version)

import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // For our custom iframe style

// --- MUI Components ---
import {
  Container, Box, Typography, Card, CardContent, CardHeader, TextField,
  ToggleButton, ToggleButtonGroup, Tooltip, IconButton, Snackbar, Alert
} from '@mui/material';
import LoadingButton from '@mui/lab/LoadingButton';

// --- MUI & Lucide Icons ---
import { Api, Code, Css, Html, Javascript } from '@mui/icons-material';
import { Copy, Download, Play, Bot } from 'lucide-react';

// --- Syntax Highlighter ---
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
// THIS IS THE CORRECTED LINE:
import { vscDarkPlus as syntaxTheme } from 'react-syntax-highlighter/dist/esm/styles/prism';

function App() {
  // --- STATE ---
  const [prompt, setPrompt] = useState('');
  const [generatedCode, setGeneratedCode] = useState('');
  const [language, setLanguage] = useState('javascript-web');
  const [loading, setLoading] = useState(false);
  const [showPreview, setShowPreview] = useState(false);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'success' });

  // --- CONFIGURATION ---
  const languages = {
    'javascript-web': { label: 'JS Web Page', icon: <Html />, syntax: 'html', extension: 'html' },
    'html': { label: 'HTML', icon: <Html />, syntax: 'markup', extension: 'html' },
    'css': { label: 'CSS', icon: <Css />, syntax: 'css', extension: 'css' },
    'javascript': { label: 'JavaScript', icon: <Javascript />, syntax: 'javascript', extension: 'js' },
    'python': { label: 'Python', icon: <Api />, syntax: 'python', extension: 'py' }
  };

  // --- HANDLERS ---
  const handleLanguageChange = (event, newLanguage) => {
    if (newLanguage !== null) {
      setLanguage(newLanguage);
    }
  };

  const handleGenerateCode = async () => {
    if (!prompt.trim()) return;
    setLoading(true);
    setGeneratedCode('');
    setShowPreview(false);
    try {
      const response = await axios.post('http://127.0.0.1:5000/api/generate', { prompt, language });
      setGeneratedCode(response.data.code);
    } catch (err) {
      setNotification({ open: true, message: 'Error generating code. Check the backend.', severity: 'error' });
    } finally {
      setLoading(false);
    }
  };

  const copyToClipboard = () => {
    if (!generatedCode) return;
    navigator.clipboard.writeText(generatedCode).then(() => {
      setNotification({ open: true, message: 'Code copied to clipboard!', severity: 'success' });
    }).catch(() => {
      setNotification({ open: true, message: 'Failed to copy code.', severity: 'error' });
    });
  };

  const downloadCode = () => {
    if (!generatedCode) return;
    const { extension } = languages[language];
    const blob = new Blob([generatedCode], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `code.${extension}`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleCloseNotification = () => {
    setNotification({ ...notification, open: false });
  };

  const canShowPreview = language === 'html' || language === 'javascript-web';

  // --- RENDER ---
  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      <Box textAlign="center" mb={5}>
        <Bot size={48} color="#90caf9" style={{ marginBottom: '8px' }} />
        <Typography variant="h4" component="h1" gutterBottom>
          AI Code Generator
        </Typography>
        <Typography variant="subtitle1" color="text.secondary">
          Professional UI with Material-UI, Flask, and Groq
        </Typography>
      </Box>

      <div className="row">
        {/* Input Column */}
        <div className="col-lg-5 mb-4 mb-lg-0">
          <Card elevation={3}>
            <CardHeader title="Your Instructions" />
            <CardContent>
              <Box mb={3}>
                <Typography variant="overline" color="text.secondary">Language</Typography>
                <ToggleButtonGroup
                  value={language}
                  exclusive
                  onChange={handleLanguageChange}
                  fullWidth
                  color="primary"
                >
                  {Object.entries(languages).map(([key, { label, icon }]) => (
                    <ToggleButton key={key} value={key}>
                      {icon}
                      <Typography variant="body2" sx={{ ml: 1, display: { xs: 'none', sm: 'block' } }}>{label}</Typography>
                    </ToggleButton>
                  ))}
                </ToggleButtonGroup>
              </Box>

              <TextField
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                label="Describe what you want to create"
                multiline
                rows={8}
                fullWidth
                placeholder="e.g., A simple todo list app with a text input and an add button"
              />

              <Box mt={3}>
                <LoadingButton
                  onClick={handleGenerateCode}
                  loading={loading}
                  variant="contained"
                  size="large"
                  fullWidth
                  disabled={!prompt.trim()}
                >
                  Generate Code
                </LoadingButton>
              </Box>
            </CardContent>
          </Card>
        </div>

        {/* Output Column */}
        <div className="col-lg-7">
          <Card elevation={3} sx={{ height: '100%' }}>
            <CardHeader
              title="Generated Code"
              action={
                generatedCode && (
                  <Box>
                    {canShowPreview && (
                      <Tooltip title={showPreview ? "Hide Preview" : "Show Preview"}>
                        <IconButton onClick={() => setShowPreview(!show.preview)} color="secondary">
                          <Play size={20} />
                        </IconButton>
                      </Tooltip>
                    )}
                    <Tooltip title="Copy to Clipboard">
                      <IconButton onClick={copyToClipboard} color="primary">
                        <Copy size={20} />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Download File">
                      <IconButton onClick={downloadCode}>
                        <Download size={20} />
                      </IconButton>
                    </Tooltip>
                  </Box>
                )
              }
            />
            <CardContent>
              <Box sx={{ height: 400, backgroundColor: '#1e2124', borderRadius: 1, overflow: 'auto' }}>
                {generatedCode ? (
                   <SyntaxHighlighter language={languages[language].syntax} style={syntaxTheme} showLineNumbers customStyle={{ margin: 0, height: '100%', background: 'transparent' }}>
                     {generatedCode}
                   </SyntaxHighlighter>
                ) : (
                  <Box display="flex" flexDirection="column" alignItems="center" justifyContent="center" height="100%" color="text.secondary">
                    <Code sx={{ fontSize: 60, mb: 2 }} />
                    <Typography>Your generated code will appear here</Typography>
                  </Box>
                )}
              </Box>

              {showPreview && canShowPreview && (
                <Box mt={3}>
                  <Typography variant="h6" gutterBottom>Live Preview</Typography>
                  <iframe
                    className="code-preview-iframe"
                    srcDoc={generatedCode}
                    title="Live Preview"
                    sandbox="allow-scripts allow-forms"
                  />
                </Box>
              )}
            </CardContent>
          </Card>
        </div>
      </div>

      <Snackbar open={notification.open} autoHideDuration={4000} onClose={handleCloseNotification} anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}>
        <Alert onClose={handleCloseNotification} severity={notification.severity} sx={{ width: '100%' }}>
          {notification.message}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
