// frontend/src/App.js (Live Preview Enabled)

import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

import {
  Container, Box, Typography, TextField, Button,
  Card, CardContent, IconButton, Snackbar, Alert
} from '@mui/material';

// --- UPDATE THIS ---: Import the PlayArrow icon
import { Code, ContentCopy, Download, PlayArrow } from '@mui/icons-material';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { vscDarkPlus as syntaxTheme } from 'react-syntax-highlighter/dist/esm/styles/prism';

function App() {
  const [selectedLanguages, setSelectedLanguages] = useState(['javascript']);
  const [prompt, setPrompt] = useState('');
  const [generatedCode, setGeneratedCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  // --- ADD THIS ---: State to control the preview panel's visibility
  const [showPreview, setShowPreview] = useState(false);

  const languages = [
    { id: 'html', label: 'HTML', icon: '🌐' },
    { id: 'javascript', label: 'JavaScript', icon: '⚡' },
    { id: 'python', label: 'Python', icon: '🐍' },
    { id: 'css', label: 'CSS', icon: '🎨' }
  ];

  // --- ADD THIS ---: Logic to determine if a preview is possible
  const canShowPreview = selectedLanguages.includes('html');

  const handleLanguageToggle = (lang) => {
    // This logic ensures at least one language is always selected
    if (selectedLanguages.includes(lang)) {
      if (selectedLanguages.length > 1) {
        setSelectedLanguages(selectedLanguages.filter(l => l !== lang));
      }
    } else {
      setSelectedLanguages([...selectedLanguages, lang]);
    }
  };

  const generateCode = async () => {
    if (!prompt.trim()) {
      setSnackbar({ open: true, message: 'Please enter a description', severity: 'warning' });
      return;
    }
    setLoading(true);
    // --- ADD THIS ---: Hide the preview on new generation
    setShowPreview(false); 
    try {
      const response = await axios.post('http://localhost:5000/api/generate', {
        prompt,
        languages: selectedLanguages
      });
      setGeneratedCode(response.data.code);
    } catch (error) {
      setSnackbar({ open: true, message: 'Error generating code.', severity: 'error' });
    }
    setLoading(false);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(generatedCode);
    setSnackbar({ open: true, message: 'Code copied!', severity: 'success' });
  };

  const downloadCode = () => {
    const element = document.createElement('a');
    const file = new Blob([generatedCode], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = `generated-code.${selectedLanguages[0]}`;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  return (
    <Container maxWidth="lg" className="app-container">
      <Box className="header">
        <Typography variant="h1"><Code /> AI Code Generator</Typography>
        <Typography variant="subtitle1">Generate code in multiple languages with AI assistance</Typography>
      </Box>

      <Card className="code-generation-card">
        <CardContent>
          <Box className="language-selector">
            {languages.map(lang => (
              <Button
                key={lang.id}
                onClick={() => handleLanguageToggle(lang.id)}
                variant={selectedLanguages.includes(lang.id) ? 'contained' : 'outlined'}
                startIcon={<span>{lang.icon}</span>}
              >
                {lang.label}
              </Button>
            ))}
          </Box>
          <TextField
            className="prompt-input" fullWidth multiline rows={4} variant="outlined"
            placeholder="E.g., Create a responsive login form with validation..."
            value={prompt} onChange={(e) => setPrompt(e.target.value)}
          />
          <Button className="generate-button" onClick={generateCode} disabled={loading} variant="contained" size="large" fullWidth>
            {loading ? 'Generating...' : '✨ Generate Code'}
          </Button>
        </CardContent>
      </Card>

      {generatedCode && (
        <Card className="code-output" sx={{ mt: 4 }}>
          <CardContent>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 1, mb: 1 }}>
              {/* --- ADD THIS ---: The Live Preview Button */}
              {canShowPreview && (
                <IconButton onClick={() => setShowPreview(!showPreview)} color="secondary">
                  <PlayArrow />
                </IconButton>
              )}
              <IconButton onClick={copyToClipboard} color="primary"><ContentCopy /></IconButton>
              <IconButton onClick={downloadCode} color="primary"><Download /></IconButton>
            </Box>
            <SyntaxHighlighter language={selectedLanguages[0]} style={syntaxTheme} showLineNumbers>
              {generatedCode}
            </SyntaxHighlighter>

            {/* --- ADD THIS ---: The Live Preview Panel */}
            {showPreview && canShowPreview && (
              <iframe
                className="code-preview-iframe"
                srcDoc={generatedCode}
                title="Live Preview"
                sandbox="allow-scripts"
              />
            )}
          </CardContent>
        </Card>
      )}

      <Box className="features-grid" sx={{ mt: 4, display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: 2 }}>
        <Card className="feature-card"><CardContent><Typography variant="h6">Multi-Language Support</Typography><Typography variant="body2">Generate code in HTML, JavaScript, Python, and CSS</Typography></CardContent></Card>
        <Card className="feature-card"><CardContent><Typography variant="h6">Live Preview</Typography><Typography variant="body2">See your HTML code rendered in real-time</Typography></CardContent></Card>
        <Card className="feature-card"><CardContent><Typography variant="h6">Export & Copy</Typography><Typography variant="body2">Easily copy or download your generated code</Typography></CardContent></Card>
      </Box>

      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={() => setSnackbar({ ...snackbar, open: false })}>
        <Alert severity={snackbar.severity}>{snackbar.message}</Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
